import './animatedtxt.sass'

const AnimatedTxt = ({ letterClass, strArray, idx }) => {
    return(
        <span>
            {
                strArray.map((char, i) =>(
                    <span key={char.id + 1} className={`${letterClass}_${i + idx}`}>
                        {char}
                    </span>
                ))
            }
        </span>
    )
}

export default AnimatedTxt
